-- Create "vulnerabilities" table
CREATE TABLE "vulnerabilities" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "vulnerability_id" character varying NOT NULL, "vulnerability_url_details" character varying NOT NULL, "installed_version" character varying NOT NULL, "fixed_version" character varying NOT NULL, "status" character varying NOT NULL, "title" character varying NOT NULL, "severity" character varying NOT NULL, "v3_score" character varying NOT NULL, PRIMARY KEY ("id"));
-- Create "vulnerability_manifests" table
CREATE TABLE "vulnerability_manifests" ("vulnerability_id" bigint NOT NULL, "manifest_id" bigint NOT NULL, PRIMARY KEY ("vulnerability_id", "manifest_id"), CONSTRAINT "vulnerability_manifests_manifest_id" FOREIGN KEY ("manifest_id") REFERENCES "manifests" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "vulnerability_manifests_vulnerability_id" FOREIGN KEY ("vulnerability_id") REFERENCES "vulnerabilities" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
