-- Create "organization_invites" table
CREATE TABLE "organization_invites" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "email" character varying NULL, "role" character varying NOT NULL, "organization_id" bigint NOT NULL, "user_id" bigint NULL, PRIMARY KEY ("id"), CONSTRAINT "organization_invites_organizations_organization_invites" FOREIGN KEY ("organization_id") REFERENCES "organizations" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "organization_invites_users_organization_invites" FOREIGN KEY ("user_id") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE SET NULL);
